export default function(singleProto){
	if( !singleProto || typeof singleProto != 'function'){
		return singleProto;
	}
	var singleProtoString = singleProto.toString();
	if( !singleProtoString.match(/apply\(this,\s*arguments\)/) ){
		return singleProto;
	}
	return async function(){
		try{
			return await singleProto.apply(this,arguments);
		}catch(e){
			alert(e);
			if( e.stack ){
				console.error(e.stack);
			}
		}
	}
}