import uedit from '../util/uedit.js';
module.exports = React.createClass({
	getDefaultProps: function () {
		return {
			style: {},
			content: '',
			width: 600,
		};
	},
	render: function () {
		var content = uedit.parse(this.props.content, this.props.width)
		if (this.props.replaceTagStyle) {
			this.props.replaceTagStyle.map((single) => {
				content = uedit.replaceTagStyle(content, single.tag, single.style)
			})
		}
		return React.createElement('div', {
			className: this.props.className,
			dangerouslySetInnerHTML: {
				__html: content
			}
		});
	}
});